function getSectionLabel (sectionSelector) {
var parts = sectionSelector.replace("#", "").split("-");
return _.map(parts, function (part) {
return part.charAt(0).toUpperCase() + part.substring(1);
}).join(" ");
}
function scrollToSection (event) {
var
targ = this.href.replace(/(.*)(#.*)$/, "$2"),
headerHeight = $(".site-header").outerHeight(),
targTop = $(targ).offset().top - headerHeight,
targLabel = getSectionLabel(targ);
BS.pubsub.fire("homepage_topnav_section_click", targLabel);
BS.pubsub.fire('autoscroll_start');
event.stopPropagation();
event.preventDefault();
$("body")
.removeClass("nav-top-stories nav-popular-stories nav-new-stories")
.addClass("nav-" + targ.replace("#", ""));
$(this).removeClass("down");
$('html,body').animate({ scrollTop : targTop}, 1000, function () {
BS.pubsub.fire('autoscroll_stop');
return false;
});
return false;
}
