/**/
Event.observe(window, "load", function() {
  var swfObject = new SWFObject("/guid.swf", "pguid", 1, 1, "1");
  swfObject.addParam("allowScriptAccess", "always");
  VersionCheck.write(swfObject, "semcheck-container");

  setTimeout(function() {
    if (location.search && location.search.indexOf("fromsearch=") > 0) {
      var sem = (/^(google|yahoo)/.test(location.search.substring(location.search.indexOf("fromsearch=" ) + "fromsearch=".length)) ? (RegExp.$1) : '');
      var pguid = document.getElementById('pguid');
      if (sem && pguid) {
        Event.observe(window, "load", function() {
          pingImage("http://t.hulu.com/beacon/v2/siteinteraction?event=searchvisit&param=" + sem + "&value=" + (pguid.hasGUID() ? 1 : 0) + "&dict=selected%3a" + pguid.getGUID());
        });
      }
    }
    Beacon.init();
  }, 500);
});
/**/

