body.script:
var greater = false;
$(document).ready(function()
{
	/* update quick contact */
	$(".contact_quick_contact label").each(function()
	{	
		var html = $(this).text();
		next = $(this).next();
		is_input = next.is("input");
		is_textarea = next.is("textarea");

		if(is_input)
		{
			next.attr("value", html);
			next.focus(function()
			{
				check = $(this).val();
				if(check == html)
					$(this).val('');
			});
			next.blur(function()
			{
				check = $(this).val();
				if(check == '')
					$(this).val(html);
			});
		}
		else if(is_textarea)
		{
			next.html(html);
			next.focus(function()
			{
				check = $(this).text();
				if(check == html)
					$(this).html('');
			});
			next.blur(function()
			{
				check = $(this).text();
				if(check == '')
					$(this).html(html);
			});
		}

		$(this).hide();
	});
});

var greater = false;
$(document).ready(function()
{
	$('#left_box_top').css('height', $('#right_box_top').height() - 15);

	$(window).scroll(function(){
		if($(window).scrollTop() > 30)
		{
			//$('.top_bar').fadeTo('slow', '.8');
			$('.sticky, #content_shell').addClass('set');
			if(!greater)
			{
				$('.top_login').animate({ 'top': '30px' }, 500, 'linear');
			}
			greater = true;
		}
		else
		{
			//$('.top_bar').css('opacity', '1');
			$('.sticky, #content_shell').removeClass('set');
			if(greater)
			{
				$('.top_login').animate({ 'top': '0' }, 500, 'linear');
			}
			greater = false;
		}
	});   	
});

